gn\":\"center\"} -->\n<p class=\"has-text-align-center\"><strong>MCA algorithme</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<center>\n<h2>Calcul d\'une moyenne de 3 notes</h2>\n</center>\n<!-- /wp:html --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><strong>Langage : C</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Ici</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><strong>Langage : PHP</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">ici</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'COURS', '', 'inherit', 'closed', 'closed', '', '243-revision-v1', '', '', '2021-10-31 01:52:23', '2021-10-30 23:52:23', '', 243, 'http://localhost/projetgroupe/?p=599', 0, 'revision', '', 0),
