preformatted -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\"><strong>&lt;?php\n$_note1 = $_POST[\'n1\'];\n$_note2 = $_POST[\'n2\'];\n$_note3 = $_POST[\'n3\'];\n$somme = $_note1 + $_note2 + $_note3;\n$moy = round($somme / 3, 2);\necho \"&lt;p&gt;Moyenne : $moy&lt;/p&gt;\";\n?&gt;</strong></pre>\n<!-- /wp:preformatted --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3} -->\n<h3 class=\"has-text-align-left\">Calcul surface &amp; périmètre</h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3} -->\n<h3 class=\"has-text-align-left\"> Calcul surface &amp; périmètre </h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3} -->\n<h3 class=\"has-text-align-left\"> Calcul surface &amp; périmètre </h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p><strong>declaration : L, l, peri, surface = réel</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>début :</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>afficher (\"saisir longueur\")</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>saisir (L)</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>afficher (\"saisir largeur\")</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>saisir (l)</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>peri &lt;- 2 * L + 2 * l</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>surface